INSERT INTO `user` VALUES ('1543980071', '1', '3', 'geekxz@aliyun.com', 'Super', '2', 'pbkdf2:sha256:50000$9EkZ4Q1C$62563672426a2df41772b438355a86b7f87a33a786094305757747980c16e0cf');
